//================================== Begin: FIFRMWARE UPDATEING ===============================================//
//MMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMM
const char Firmware_html[] = R"rawliteral(
<!DOCTYPE html>
<html lang="en">
<head>
    <meta name="viewport" content="width=device-width,user-scalable=0" charset="UTF-8">    
    <title>OTA Firmware Update </title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background: #f0f2f5;
            text-align: center;
            margin: 40px;
        }
        h3 {
            color: #333;
        }
    </style>
</head>
<body>
    <div style="text-align: center">
        <header>    <div style="text-align: center">
      <header>
                        <img src="data:image/png;base64," 
                        alt="Logo" style="width: 400px; height: auto;">
					</header>
    </div>
      <div id="updateFirmware" style="text-align: center;">
        <h3>Upload Firmware (.bin)</h3>
        <input type="file" id="firmwareFile" accept=".bin">
        <button class="NutNhan" onclick="UpdateFirmware()">Cập nhật firmware</button>

        <div id="progressContainer" style="width: 100%; background: #eee; margin-top: 15px;">
          <div id="progressBar" style="width: 0%; background: green; color: white; text-align:center">0%</div>
        </div>

        <div id="ThongBao1"></div>
        <div id="ThongBao2"></div>
      </div>

      <script>
      function UpdateFirmware() {
        const fileInput   = document.getElementById("firmwareFile");
        const progressBar = document.getElementById("progressBar");

        if (!fileInput.files.length) {
          alert("⚠ Vui lòng chọn file .bin trước!");
          return;
        }
        const file = fileInput.files[0];
        const formData = new FormData();
        formData.append("firmware", file);

        const xhr = new XMLHttpRequest();
        xhr.open("POST", "/update", true);

        // Reset progress
        progressBar.style.width = "0%";
        progressBar.innerText   = "0%";
        document.getElementById("ThongBao1").innerHTML = "Đang upload firmware...";

        xhr.upload.onprogress = function(event) {
          if (event.lengthComputable) {
            let percent = Math.round((event.loaded / event.total) * 100);
            progressBar.style.width = percent + "%";
            progressBar.innerText   = percent + "%";
            let loadedKB = (event.loaded / 1024).toFixed(1);
            let totalKB  = (event.total / 1024).toFixed(1);

            let loadedMB = (event.loaded / (1024*1024)).toFixed(2);
            let totalMB  = (event.total / (1024*1024)).toFixed(2);
          
            document.getElementById("ThongBao2").innerText = `Đã upload: ${loadedKB} / ${totalKB} KB (${percent}%)`;
          }
        };
        xhr.onload =  function() {
              if(xhr.status == 200){
                document.getElementById("ThongBao1").innerHTML = "✅ Upload thành công! ESP32 sẽ khởi động lại...";
              } else {
                document.getElementById("ThongBao1").innerHTML = "❌ Lỗi upload!";
              };
            };
        xhr.send(formData);
      }
      </script>
</body>
</html>
)rawliteral";


